import React, { useState } from 'react'
import './App.css'
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';

const App = () => {

    const [formValues, setFormValues] = useState([{ name: "", type: ""}])
    const [show, setShow] = useState(false);
    const [fieldType, setFieldType] = useState('simple')

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    let handleChange = (i, e) => {
        let newFormValues = [...formValues];
        newFormValues[i][e.target.name] = e.target.value;
        setFormValues(newFormValues);
      }

    let addFormFields = () => {
        setShow(false);
        setFormValues([...formValues, { name: "", type: "simple" }])
      }

    let removeFormFields = (i) => {
        let newFormValues = [...formValues];
        newFormValues.splice(i, 1);
        setFormValues(newFormValues)
    }

    let handleSubmit = (event) => {
        event.preventDefault();
        alert(JSON.stringify(formValues));
    }

    return (
      <>
        <Button variant="primary" onClick={handleShow}>
          Launch demo modal
        </Button>

        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>
          <div>
            <input type="radio" value="simple" name="field" onChange={e=> setFieldType(e.target.value) } /> Simple
            <input type="radio" value="complex" name="field" onChange={e=> setFieldType(e.target.value) }/> Complex
          </div>
              </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={() => addFormFields()}>
              Submit
            </Button>
          </Modal.Footer>
        </Modal>

        <form  onSubmit={handleSubmit}>
          {formValues.map((element, index) => (
            <div className="form-inline" key={index}>
              {element.type === 'simple' &&
                <h2>Assess Structure</h2>
              }
              {element.type === 'complex' &&
                <input type="text" name="name" value={element.name || ""} onChange={e => handleChange(index, e)} />
              }

              {
                index ? 
                  <button type="button"  className="button remove" onClick={() => removeFormFields(index)}>Remove</button> 
                : null
              }
            </div>
          ))}
          <div className="button-section">
              <button className="button add" type="button" onClick={() => addFormFields()}>Add</button>
              <button className="button submit" type="submit">Submit</button>
          </div>
        </form>
      </>
    )
}

export default App
