import React, { useState } from "react";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash, faCirclePlus } from "@fortawesome/free-solid-svg-icons";

import Simple from "./Simple";

const Complex = (props) => {
	const [count, setCount] = useState(0);
	return (
		<div className="paper-shadow-z-2">
			<ul>
				<div class="rectangle-cables">
					<input type="text" placeholder="Cables" id="cables" />
					{true ? (
						<button
							type="button"
							className="button ExtraButton Btn-cables"
							onClick={() => props.removeElement(props.item.id)}
						>
							<FontAwesomeIcon icon={faTrash} />
						</button>
					) : null}
				</div>
				{props.item.childItems.map((ci) => {
					return (
						<ItemRow
							ci={ci}
							parent={props.item}
							workaroundfncall={() => {
								setCount((count) => count + 1);
							}}
							addChildElement={props.addChildElement}
						/>
					);
				})}
				<div>
					<button
						className="button add"
						type="button"
						onClick={() => props.addChildElement(props.item)}
					>
						<FontAwesomeIcon icon={faCirclePlus} />
					</button>
				</div>
			</ul>
		</div>
	);
};

const ItemRow = (props) => {
	return (
		<li key={props.ci.id}>
			<div className="paper-shadow-z-2">
				<hr />
				<span class="or">Or</span>
				{props.ci.type === "simple" && (
					<Simple
						item={props.ci}
						removeElement={() => {
							let children = props.parent.childItems;
							let updatedChildren = children.filter(
								(c) => c.id !== props.ci.id
							);
							props.parent.childItems = updatedChildren;
							props.workaroundfncall();
						}}
					/>
				)}

				{props.ci.type === "complex" && (
					<Complex
						item={props.ci}
						removeElement={() => {
							let children = props.parent.childItems;
							let updatedChildren = children.filter(
								(c) => c.id !== props.ci.id
							);
							props.parent.childItems = updatedChildren;
							props.workaroundfncall();
						}}
						addChildElement={() => {
							props.addChildElement(props.ci);
							props.workaroundfncall();
						}}
					/>
				)}
			</div>
		</li>
	);
};

export default Complex;
