import React from "react";
// import image from  '../images/icon-delete/delete_24px@1x.svg'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTrash,faCirclePlus } from '@fortawesome/free-solid-svg-icons'
//import { useState } from "react";

class New extends React.Component {
    state = {
      rows: [{}]
    };

    handleChange = index => e => {
        const { name, value } = e.target;
        const rows = [...this.state.rows];
        rows[index] = {
          [name]: value
        };
        this.setState({
          rows
        });
      };
      handleAddRow = () => {
        const element = {};
        this.setState({
          rows: [...this.state.rows, element]
        });
      };
      handleRemoveRow = () => {
        this.setState({
          rows: this.state.rows.slice(0, -1)
        });
      };
    render() {
        return(
            <div className="paper-shadow-z-2">
                <ul>
                    <div class = "rectangle-cables">
                        <input type="text" placeholder="Cables" id="cables" />
                        {
                        this.index ? 
                          <button type="button"  className="button ExtraButton Btn-cables" onClick={this.handleRemoveRow}><FontAwesomeIcon icon={faTrash} /></button> 
                        : null
                      }
                    </div>   
                <li>
                    <div class="rectangle">
                    {this.state.rows.map((element, index) => (
                            <input
                                    type="text"
                                    name="name"
                                    value={this.state.rows[index].name}
                                    onChange={this.handleChange(index)}
                                    className="form-control"
                                    />
                        ))}
                        <button type="button"  className="button ExtraButton Btn-multiple" onClick={this.handleRemoveRow}><FontAwesomeIcon icon={faTrash} /></button> 
                    </div>
                </li>
                </ul>
                <div>
                <button className="button add Multi-Button" type="button" onClick={this.handleAddRow}><FontAwesomeIcon icon={faCirclePlus} /></button>
            </div>
                </div>
        )
}
}

export default New;
