import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTrash,faCirclePlus } from '@fortawesome/free-solid-svg-icons'
import { useState } from "react";



const MC = ({ index, props }) => {
    const [show, setShow] = useState(false);
    const handleShow = () => setShow(true);
    return(
        // <div className="component2">
            <div className="paper-shadow-z-2">
                <ul>
                    <div class = "rectangle-cables">
                        <input type="text" placeholder="Cables" id="cables" />
                        {
                        index ? 
                          <button type="button"  className="button ExtraButton Btn-cables" onClick={(e) => props.removeFormFields(index,e)}><FontAwesomeIcon icon={faTrash} /></button> 
                        : null
                      }
                    </div>   
                <li>

                    <div class="rectangle">
                        <input type="text" placeholder="Copper" id="copper" />

                          <button type="button"  className="button ExtraButton Btn-multiple" onClick={(e) => props.removeFormFields(index,e)}><FontAwesomeIcon icon={faTrash} /></button> 

                    </div>
                    <hr />
                    <div class="rectangle">
                        <input type="text" placeholder="Fibre" id="copper" />

                          <button type="button"  className="button ExtraButton Btn-multiple" onClick={(e) => props.removeFormFields(index,e)}><FontAwesomeIcon icon={faTrash} /></button> 

                    </div>
                    <hr />
                </li>
                </ul>
                <div>
                <button className="button add Multi-Button" type="button" onClick={handleShow}><FontAwesomeIcon icon={faCirclePlus} /></button>
                {show ? <MyModal handleShow={() => setShow(false)} /> : null}
            </div>
            </div> 
        // </div>    
    );
}

export default MC;
